import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import Login from "./Login";
import Register from "./Register";

const BACKEND_URL = "http://localhost:8000"; // Make sure this matches your backend URL

function App() {
  const [token, setToken] = useState(localStorage.getItem("token") || "");
  const [chatId, setChatId] = useState(null);
  const [messages, setMessages] = useState([]);
  const [userInput, setUserInput] = useState("");
  const [conversations, setConversations] = useState([]);
  const [view, setView] = useState("login"); // "login" or "register" or "chat"
  const [username, setUsername] = useState("");
  const [isLoading, setIsLoading] = useState(false);


  const inputRef = useRef(null);

  useEffect(() => {
    if (token) {
      localStorage.setItem("token", token);

      axios
        .get(`${BACKEND_URL}/conversations`, {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((res) => setConversations(res.data))
        .catch(() => {
          setView("login");
        });
    }
  }, [token]);


  useEffect(() => {
    if (inputRef.current) {
      inputRef.current.style.height = "auto";
      inputRef.current.style.height = inputRef.current.scrollHeight + "px";
    }
  }, [userInput]);

  const sendMessage = () => {
    if (!userInput.trim()) return;
    if (!token) {
      alert("Please login first.");
      return;
    }

    setIsLoading(true);

    axios
      .post(
        `${BACKEND_URL}/chat`,
        { message: userInput, chat_id: chatId },
        { headers: { Authorization: `Bearer ${token}` } }
      )
      .then((res) => {
        setMessages((prevMessages) => {
          // Filter out any assistant welcome message if it was removed from backend response
          const historyFromServer = res.data.history;
          const newMessages = historyFromServer.slice(-2); // user + assistant

          return [...prevMessages, ...newMessages];
        });

        setChatId(res.data.chat_id);
        setUserInput("");
      })
      .catch((err) => {
        console.error("Error sending message:", err);
        if (err.response?.status === 401) {
          alert("Session expired or unauthorized. Please log in again.");
          localStorage.removeItem("token");
          setToken("");
          setView("login");
        } else {
          alert("Failed to send message.");
        }
      })
      .finally(() => setIsLoading(false));
  };


  const startNewChat = () => {
    if (!token) {
      alert("Please login first.");
      return;
    }

    axios
      .post(
        `${BACKEND_URL}/chat/start`,
        {},
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      )
      .then((res) => {
        setChatId(res.data.chat_id);
        setUserInput("");
        setMessages([
            {
              role: "assistant",
              content: "Hi! Welcome to ALAASKA. How can I help you today?",
            },
        ]);
      })
      .catch((err) => {
        console.error("Error starting new chat:", err);
        alert("Failed to start a new chat.");
      });
  };

  const loadConversation = (id) => {
    if (!token) {
      alert("Please login first.");
      return;
    }

    axios
      .get(`${BACKEND_URL}/conversation/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((res) => {
        setMessages(res.data);
        setChatId(id);
      })
      .catch((err) => {
        console.error("Error loading conversation:", err);
        alert("Failed to load conversation.");
      });
  };

  const deleteConversation = (idToDelete) => {
    const confirmed = window.confirm(
      "Are you sure you want to delete this conversation?"
    );
    if (!confirmed) return;

    axios
      .delete(`${BACKEND_URL}/conversation/${idToDelete}`, {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then(() => {
        // Clear chat if the deleted one is currently loaded
        if (chatId === idToDelete) {
          setMessages([]);
          setChatId(null);
        }
        setConversations(
          conversations.filter((item) => item.chat_id !== idToDelete)
        );
      })
      .catch((err) => {
        console.error("Error deleting conversation:", err);
        alert("Failed to delete the conversation.");
      });
  };

  if (!token) {
    return (
      <div className="auth-container">
        {view === "login" ? (
          <Login setToken={setToken} setView={setView} setUsername={setUsername} />
        ) : (
          <Register setView={setView} />
        )}
      </div>
    );
  }

  return (
    <>
      <div className="app-wrapper">
        {/* Sidebar stays full height, separate column */}
        <div className="sidebar">
          <button
            className="new-chat-button"
            onClick={startNewChat}
            title="Start new chat"
          >
            {/* SVG */}
            <svg
              xmlns="http://www.w3.org/2000/svg"
              width="24"
              height="24"
              fill="none"
              stroke="currentColor"
              strokeWidth="1.8"
              strokeLinecap="round"
              strokeLinejoin="round"
              viewBox="0 0 24 24"
              aria-hidden="true"
              focusable="false"
            >
              <rect x="3" y="4" width="14" height="16" rx="2" ry="2" />
              <path d="M17 7l-5 5-1 3 3-1 6-6-3-3z" />
            </svg>
            {" "}New Chat
          </button>

          <h3 className="past-conversations-title">Past Conversations</h3>
          {conversations.length === 0 && <p>No chats yet.</p>}
          {conversations.map((c) => (
            <div
              key={c.chat_id}
              style={{
                display: "flex",
                justifyContent: "space-between",
              }}
            >
              <button
                className="conversation-button"
                onClick={() => loadConversation(c.chat_id)}
                style={{ flex: 1, marginRight: "0.5rem" }}
                title={c.summary}
              >
                {c.summary}
              </button>
              <button
                className="icon-button delete-button"
                onClick={() => deleteConversation(c.chat_id)}
                title="Delete conversation"
                aria-label="Delete conversation"
              >
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  width="16"
                  height="16"
                  fill="black"
                  viewBox="0 0 24 24"
                  aria-hidden="true"
                  focusable="false"
                >
                  <path d="M9 3v1H4v2h16V4h-5V3H9zm2 5v10h2V8h-2zm-4 0v10h2V8H7zm10 0v10h-2V8h2z" />
                </svg>
              </button>
            </div>
          ))}
        </div>

        {/* Right side of app: top bar + main content + footer */}
        <div className="content-area">
          <div className="top-bar">
            <h1>ALAASKA – AI Tutor</h1>
            <div style={{ display: "flex", alignItems: "center", gap: "0.5rem" }}>
              <div
                style={{
                    backgroundColor: "lightseagreen",
                    color: "white",
                    borderRadius: "50%",
                    width: "32px",
                    height: "32px",
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "center",
                    fontWeight: "bold",
                    fontSize: "14px",
                    textTransform: "uppercase",
                }}
                title={username}
                >
                {username.slice(0, 2)}
              </div>

                <span style={{ color: "black", fontWeight: "500" }}>{username}</span>
                <button
                    style={{ backgroundColor: "black", color: "white", border: "none", padding: "0.4rem 0.8rem", borderRadius: "4px" }}
                    onClick={() => {
                    localStorage.removeItem("token");
                    setToken("");
                    setUsername("");
                    setView("login");
                    }}
                >
                    Logout
                </button>
            </div>

          </div>

          <div className="container">
            <div className="main">
              <div className="chatbox">
                <div className="messages">
                  {messages.map((msg, i) => (
                    <div
                      key={i}
                      className={`message ${msg.role === "user" ? "user-message" : "assistant-message"}`}
                    >
                      {msg.content}
                    </div>
                ))}
                {isLoading && (
                  <div className="message assistant-message">
                    <em>Assistant is typing...</em>
                  </div>
                  )}
                </div>

                <div className="input-container">
                  <textarea
                    ref={inputRef}
                    placeholder="Ask something..."
                    value={userInput}
                    onChange={(e) => setUserInput(e.target.value)}
                    onKeyDown={(e) => {
                      if (e.key === "Enter" && !e.shiftKey) {
                        e.preventDefault();
                        sendMessage();
                      }
                    }}
                    rows={3}
                    maxLength={1000}
                  />
                  <button
                    className="send-button"
                    onClick={sendMessage}
                    title="Send"
                    aria-label="Send message"
                  >
                    <svg
                      xmlns="http://www.w3.org/2000/svg"
                      width="20"
                      height="20"
                      fill="currentColor"
                      viewBox="0 0 16 16"
                    >
