import React, { useState } from "react";
import axios from "axios";

const BACKEND_URL = "http://localhost:8000";

function Login({ onLogin }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    try {
      const params = new URLSearchParams();
      params.append("username", username);
      params.append("password", password);
      const res = await axios.post(`${BACKEND_URL}/token/`, params, {
        headers: { "Content-Type": "application/x-www-form-urlencoded" },
      });
      onLogin(username, res.data.access_token);
    } catch {
      setError("Login failed. Please check your credentials.");
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        placeholder="Username"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
        required
      />
      <input
        placeholder="Password"
        type="password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        required
      />
      <button type="submit">Login</button>
      {error && <p style={{ color: "red" }}>{error}</p>}
    </form>
  );
}

function App() {
  const [token, setToken] = useState(localStorage.getItem("token") || null);
  const [username, setUsername] = useState(localStorage.getItem("username") || "");
  const [chatHistory, setChatHistory] = useState([]);
  const [userInput, setUserInput] = useState("");
  const [isSending, setIsSending] = useState(false);

  const handleLogin = (user, token) => {
    setUsername(user);
    setToken(token);
    localStorage.setItem("token", token);
    localStorage.setItem("username", user);
  };

  const handleLogout = () => {
    setUsername("");
    setToken(null);
    setChatHistory([]);
    localStorage.removeItem("token");
    localStorage.removeItem("username");
  };

  const sendMessage = async () => {
    if (!userInput.trim() || isSending) return;
    setIsSending(true);
    setChatHistory((prev) => [...prev, { role: "user", content: userInput }]);

    try {
      const res = await axios.post(
        `${BACKEND_URL}/chat/`,
        { message: userInput },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setChatHistory((prev) => [...prev, { role: "assistant", content: res.data.response }]);
      setUserInput("");
    } catch (err) {
      alert("Failed to send message. Check your network or login status.");
      console.error(err);
    } finally {
      setIsSending(false);
    }
  };

  if (!token) {
    return <Login onLogin={handleLogin} />;
  }

  return (
    <div style={{ maxWidth: 600, margin: "2rem auto", fontFamily: "Arial, sans-serif" }}>
      <h1>ALAASKA – AI Tutor</h1>
      <button onClick={handleLogout} style={{ marginBottom: "1rem" }}>
        Logout
      </button>

      <div style={{ border: "1px solid #ccc", padding: "1rem", height: 400, overflowY: "auto" }}>
        {chatHistory.length === 0 && <p>Ask me about system programming!</p>}

        {chatHistory.map((msg, i) => (
          <div
            key={i}
            style={{
              textAlign: msg.role === "user" ? "right" : "left",
              margin: "0.5rem 0",
            }}
          >
            <strong>{msg.role === "user" ? "You" : "ALAASKA"}:</strong> {msg.content}
          </div>
        ))}
      </div>

      <input
        type="text"
        placeholder="Type your question..."
        value={userInput}
        onChange={(e) => setUserInput(e.target.value)}
        onKeyDown={(e) => {
          if (e.key === "Enter") {
            e.preventDefault();
            sendMessage();
          }
        }}
        style={{ width: "100%", padding: "0.5rem", marginTop: "1rem" }}
        disabled={isSending}
      />
      <button
        onClick={sendMessage}
        disabled={isSending || !userInput.trim()}
        style={{ marginTop: "0.5rem", padding: "0.5rem 1rem" }}
      >
        {isSending ? "Sending..." : "Send"}
      </button>
    </div>
  );
}

export default App;
