import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Login from './Login';
import Register from './Register';

const BACKEND_URL = "http://localhost:8000";

function App() {
  const [token, setToken] = useState(localStorage.getItem("token") || "");
  const [chatId, setChatId] = useState(null);
  const [messages, setMessages] = useState([]);
  const [userInput, setUserInput] = useState("");
  const [conversations, setConversations] = useState([]);
  const [view, setView] = useState("login"); // "login" or "register" or "chat"

  useEffect(() => {
    if (token) {
      axios.get(`${BACKEND_URL}/conversations`, {
        headers: { Authorization: `Bearer ${token}` }
      })
      .then(res => setConversations(res.data))
      .catch(() => setView("login"));
    }
  }, [token]);

  const sendMessage = () => {
    if (!userInput) return;
    axios.post(`${BACKEND_URL}/chat`, {
      message: userInput,
      chat_id: chatId
    }, {
      headers: { Authorization: `Bearer ${token}` }
    }).then(res => {
      setMessages(res.data.history);
      setChatId(res.data.chat_id);
      setUserInput("");
    });
  };

  const startNewChat = () => {
    axios.post(`${BACKEND_URL}/chat/start`, {}, {
      headers: { Authorization: `Bearer ${token}` }
    }).then(res => {
      setChatId(res.data.chat_id);
      setMessages(res.data.history);
      setUserInput("");
    });
  };  

  const loadConversation = (c) => {
    axios.get(`${BACKEND_URL}/conversation/${c}`, {
      headers: { Authorization: `Bearer ${token}` }
    }).then(res => {
      setMessages(res.data);
      setChatId(c);
    });
  };

  const deleteConversation = (c) => {
    axios.delete(`${BACKEND_URL}/conversation/${c}`, {
      headers: { Authorization: `Bearer ${token}` }
    }).then(() => {
      if (c === chatId) {
        setMessages([]);
        setChatId(null);
      }
      setConversations(conversations.filter(item => item !== c));
    });
  };

  if (!token) {
    return (
      <div className="auth-container">
        {view === "login" ? (
          <Login setToken={setToken} setView={setView} />
        ) : (
          <Register setView={setView} />
        )}
      </div>
    );
  }
  return (
    <div className="container">
      <div className="top-bar">
        <h1>ALAASKA – AI Tutor</h1>
        <button
            onClick={() => {
            localStorage.removeItem("token");
            setToken("");
            setView("login");
            }}
        >
            Logout
        </button>
      </div>

      <div className="sidebar">
        <button
          onClick={startNewChat}
          style={{ marginBottom: "1rem", width: "100%" }}
        >
          ➕ New Chat
        </button>
        <h3>Past Conversations</h3>
        {conversations.length === 0 && <p>No chats yet.</p>}
        {conversations.map((c) => (
          <div
            key={c}
            style={{
              display: "flex",
              justifyContent: "space-between",
              marginBottom: "0.5rem",
            }}
          >
            <button
              onClick={() => loadConversation(c)}
              style={{ flex: 1, marginRight: "0.5rem" }}
            >
              {c}
            </button>
            <button
              onClick={() => deleteConversation(c)}
              className="icon-button"
              title="Delete Conversation"
              aria-label="Delete Conversation"
            >
              <Trash2 size={16} />
            </button>
          </div>
        ))}
      </div>

      <div className="chatbox">
        <div className="messages">
          {messages.map((msg, i) => (
            <div key={i} className={msg.role}>
              <strong>{msg.role}:</strong> {msg.content}
            </div>
          ))}
        </div>
        <input
          type="text"
          placeholder="Ask something..."
          value={userInput}
          onChange={(e) => setUserInput(e.target.value)}
          onKeyDown={(e) => e.key === "Enter" && sendMessage()}
        />
        <button onClick={sendMessage}>Send</button>
      </div>
    </div>
  );  

}

export default App;
