import React, { useState } from "react";
import axios from "axios";

const BACKEND_URL = "http://localhost:8000";

function App() {
  const [userInput, setUserInput] = useState("");
  const [chatHistory, setChatHistory] = useState([]);
  const [isSending, setIsSending] = useState(false);

  const sendMessage = async () => {
    if (!userInput.trim() || isSending) return; // Prevent empty or duplicate sends

    setIsSending(true);

    // Add user's message once
    setChatHistory((prev) => [...prev, { role: "user", content: userInput }]);

    try {
      const response = await axios.post(`${BACKEND_URL}/chat/`, {
        message: userInput,
      });

      const botReply = response.data.response;
      setChatHistory((prev) => [...prev, { role: "assistant", content: botReply }]);
      setUserInput("");
    } catch (error) {
      console.error("Error sending message:", error);
      alert("Failed to send message. Check backend and network.");
    } finally {
      setIsSending(false);
    }
  };

  return (
    <div style={{ maxWidth: 600, margin: "2rem auto", fontFamily: "Arial, sans-serif" }}>
      <h1>ALAASKA â€“ AI Tutor</h1>

      <div style={{ border: "1px solid #ccc", padding: "1rem", height: 400, overflowY: "auto" }}>
        {chatHistory.length === 0 && <p>Ask me about system programming!</p>}

        {chatHistory.map((msg, i) => (
          <div
            key={i}
            style={{
              textAlign: msg.role === "user" ? "right" : "left",
              margin: "0.5rem 0",
            }}
          >
            <strong>{msg.role === "user" ? "You" : "ALAASKA"}:</strong>{" "}
            <span>{msg.content}</span>
          </div>
        ))}
      </div>

      <input
        type="text"
        placeholder="Type your question..."
        value={userInput}
        onChange={(e) => setUserInput(e.target.value)}
        onKeyDown={(e) => {
          if (e.key === "Enter") {
            e.preventDefault(); // Prevent default form submit or multiple triggers
            sendMessage();
          }
        }}
        style={{ width: "100%", padding: "0.5rem", marginTop: "1rem" }}
        disabled={isSending}
      />
      <button
        onClick={sendMessage}
        disabled={isSending || !userInput.trim()}
        style={{ marginTop: "0.5rem", padding: "0.5rem 1rem" }}
      >
        {isSending ? "Sending..." : "Send"}
      </button>
    </div>
  );
}

export default App;
