import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import Login from "./Login";
import Register from "./Register";

const BACKEND_URL = "http://localhost:8000"; // Make sure this matches your backend URL

function App() {
  const [token, setToken] = useState(localStorage.getItem("token") || "");
  const [chatId, setChatId] = useState(null);
  const [messages, setMessages] = useState([]);
  const [userInput, setUserInput] = useState("");
  const [conversations, setConversations] = useState([]);
  const [view, setView] = useState("login"); // "login" or "register" or "chat"
  const [username, setUsername] = useState("");
  const [isLoading, setIsLoading] = useState(false);

  const inputRef = useRef(null);

  useEffect(() => {
    if (token) {
      localStorage.setItem("token", token);
      axios
        .get(`${BACKEND_URL}/conversations`, {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((res) => setConversations(res.data))
        .catch(() => setView("login"));
    }
  }, [token]);

  useEffect(() => {
    if (inputRef.current) {
      inputRef.current.style.height = "auto";
      inputRef.current.style.height = inputRef.current.scrollHeight + "px";
    }
  }, [userInput]);

  const sendMessage = () => {
    if (!userInput.trim()) return;
    if (!token) {
      alert("Please login first.");
      return;
    }

    setIsLoading(true);

    axios
      .post(
        `${BACKEND_URL}/chat`,
        {
          message: userInput,
          chat_id: chatId,
        },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      )
      .then((res) => {
        setMessages(res.data.history);
        setChatId(res.data.chat_id);
        setUserInput("");
        setIsLoading(false);

        setTimeout(() => {
          const msgContainer = document.querySelector(".messages");
          if (msgContainer) {
            msgContainer.scrollTop = msgContainer.scrollHeight;
          }
        }, 100);
      })
      .catch((err) => {
        console.error("Error sending message:", err);
        setIsLoading(false);
        if (err.response?.status === 401) {
          alert("Session expired or unauthorized. Please log in again.");
          localStorage.removeItem("token");
          setToken("");
          setView("login");
        } else {
          alert("Failed to send message.");
        }
      });
  };

  const startNewChat = () => {
    if (!token) {
      alert("Please login first.");
      return;
    }

    axios
      .post(
        `${BACKEND_URL}/chat/start`,
        {},
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      )
      .then((res) => {
        setChatId(res.data.chat_id);
        setMessages(res.data.history);
        setUserInput("");
      })
      .catch((err) => {
        console.error("Error starting new chat:", err);
        alert("Failed to start a new chat.");
      });
  };

  const loadConversation = (id) => {
    if (!token) {
      alert("Please login first.");
      return;
    }

    axios
      .get(`${BACKEND_URL}/conversation/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((res) => {
        setMessages(res.data);
        setChatId(id);

        setTimeout(() => {
          const msgContainer = document.querySelector(".messages");
          if (msgContainer) {
            msgContainer.scrollTop = msgContainer.scrollHeight;
          }
        }, 100);
      })
      .catch((err) => {
        console.error("Error loading conversation:", err);
        alert("Failed to load conversation.");
      });
  };

  const deleteConversation = (idToDelete) => {
    const confirmed = window.confirm(
      "Are you sure you want to delete this conversation?"
    );
    if (!confirmed) return;

    axios
      .delete(`${BACKEND_URL}/conversation/${idToDelete}`, {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then(() => {
        if (chatId === idToDelete) {
          setMessages([]);
          setChatId(null);
        }
        setConversations(
          conversations.filter((item) => item.chat_id !== idToDelete)
        );
      })
      .catch((err) => {
        console.error("Error deleting conversation:", err);
        alert("Failed to delete the conversation.");
      });
  };

  if (!token) {
    return (
      <div className="auth-container">
        {view === "login" ? (
          <Login
            setToken={setToken}
            setView={setView}
            setUsername={setUsername}
          />
        ) : (
          <Register setView={setView} />
        )}
      </div>
    );
  }

  return (
    <>
      <div className="app-wrapper">
        {/* Sidebar and TopBar omitted for brevity */}

        <div className="container">
          <div className="main">
            <div className="chatbox">
              <div className="messages">
                {messages.map((msg, i) => (
                  <div key={i} className={`message ${msg.role}`}>
                    <div className="bubble">{msg.content}</div>
                  </div>
                ))}
                {isLoading && (
                  <div className="message assistant loading">
                    <div className="bubble">Typing...</div>
                  </div>
                )}
              </div>

              <div className="input-container">
                <textarea
                  ref={inputRef}
                  placeholder="Ask something..."
                  value={userInput}
                  onChange={(e) => setUserInput(e.target.value)}
                  onKeyDown={(e) => {
                    if (e.key === "Enter" && !e.shiftKey) {
                      e.preventDefault();
                      sendMessage();
                    }
                  }}
                  rows={3}
                  maxLength={1000}
                />
                <button
                  className="send-button"
                  onClick={sendMessage}
                  title="Send"
                  aria-label="Send message"
                >
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    width="20"
                    height="20"
                    fill="currentColor"
                    viewBox="0 0 16 16"
                  >
                    <path d="M15.854.146a.5.5 0 0 1 .11.54l-6 14a.5.5 0 0 1-.948-.032l-2-6-6-2a.5.5 0 0 1 .032-.948l14-6a.5.5 0 0 1 .806.44zM6.832 8.065l1.476 4.427 4.318-10.078L6.832 8.065z" />
                  </svg>
                </button>
              </div>
            </div>
          </div>
        </div>

        <div className="footer">ALAASKA: Adaptive Learning</div>
      </div>
    </>
  );
}

export default App;
