import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import Login from "./Login";
import Register from "./Register";

const BACKEND_URL = "http://localhost:8000";

function App() {
  const [token, setToken] = useState(localStorage.getItem("token") || "");
  const [chatId, setChatId] = useState(null);
  const [messages, setMessages] = useState([]);
  const [userInput, setUserInput] = useState("");
  const [conversations, setConversations] = useState([]);
  const [view, setView] = useState("login"); // "login" or "register" or "chat"

  const inputRef = useRef(null);

  useEffect(() => {
    if (token) {
      axios
        .get(`${BACKEND_URL}/conversations`, {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((res) => setConversations(res.data))
        .catch(() => setView("login"));
    }
  }, [token]);

  useEffect(() => {
    if (inputRef.current) {
      inputRef.current.style.height = "auto";
      inputRef.current.style.height = inputRef.current.scrollHeight + "px";
    }
  }, [userInput]);

  const sendMessage = () => {
    if (!userInput.trim()) return;
    axios
      .post(
        `${BACKEND_URL}/chat`,
        {
          message: userInput,
          chat_id: chatId,
        },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      )
      .then((res) => {
        setMessages(res.data.history);
        setChatId(res.data.chat_id);
        setUserInput("");
      });
  };

  const startNewChat = () => {
    axios
      .post(
        `${BACKEND_URL}/chat/start`,
        {},
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      )
      .then((res) => {
        setChatId(res.data.chat_id);
        setMessages(res.data.history);
        setUserInput("");
      });
  };

  const loadConversation = (c) => {
    axios
      .get(`${BACKEND_URL}/conversation/${c}`, {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((res) => {
        setMessages(res.data);
        setChatId(c);
      });
  };

  const deleteConversation = (chatId) => {
    const confirmed = window.confirm("Are you sure you want to delete this conversation?");
    if (!confirmed) return;

    axios
      .delete(`${BACKEND_URL}/conversation/${chatId}`, {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then(() => {
        if (chatId === chatId) {
          setMessages([]);
          setChatId(null);
        }
        setConversations(conversations.filter((item) => item.chat_id !== chatId));
      })
      .catch((err) => {
        console.error("Error deleting conversation:", err);
        alert("Failed to delete the conversation.");
      });
  };  

  if (!token) {
    return (
      <div className="auth-container">
        {view === "login" ? (
          <Login setToken={setToken} setView={setView} />
        ) : (
          <Register setView={setView} />
        )}
      </div>
    );
  }

  return (
    <>
      <div className="layout">
        {/* Sidebar (on the left, independent of top bar) */}
        <div className="sidebar">
          <button onClick={startNewChat} style={{ marginBottom: "1rem" }}>
            ➕ New Chat
          </button>
          <h3>Past Conversations</h3>
          {conversations.length === 0 && <p>No chats yet.</p>}
          {conversations.map((c) => (
            <div
              key={c.chat_id}
              style={{
                display: "flex",
                justifyContent: "space-between",
                marginBottom: "0.5rem",
              }}
            >
              <button
                onClick={() => loadConversation(c.chat_id)}
                style={{ flex: 1, marginRight: "0.5rem" }}
              >
                {c.summary}
              </button>
              <button
                onClick={() => {
                  if (
                    window.confirm(
                      "Are you sure you want to delete this conversation?"
                    )
                  ) {
                    deleteConversation(c.chat_id);
                  }
                }}
                style={{
                  background: "none",
                  border: "none",
                  padding: 0,
                  cursor: "pointer",
                }}
                title="Delete conversation"
              >
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  width="16"
                  height="16"
                  fill="black"
                  viewBox="0 0 24 24"
                >
                  <path d="M9 3v1H4v2h16V4h-5V3H9zm2 5v10h2V8h-2zm-4 0v10h2V8H7zm10 0v10h-2V8h2z" />
                </svg>
              </button>
            </div>
          ))}
        </div>

        {/* Right content area: top bar + chat */}
        <div className="right-section">
          <div className="top-bar">
            <h1>ALAASKA – AI Tutor</h1>
            <button
              onClick={() => {
                localStorage.removeItem("token");
                setToken("");
                setView("login");
              }}
            >
              Logout
            </button>
          </div>

          <div className="chatbox">
            <div className="messages">
              {messages.map((msg, i) => (
                <div key={i} className={msg.role}>
                  <strong>{msg.role}:</strong> {msg.content}
                </div>
              ))}
            </div>

            <div className="input-container">
              <textarea
                ref={inputRef}
                placeholder="Ask something..."
                value={userInput}
                onChange={(e) => setUserInput(e.target.value)}
                onKeyDown={(e) => {
                  if (e.key === "Enter" && !e.shiftKey) {
                    e.preventDefault();
                    sendMessage();
                  }
                }}
                rows={3}
              />
              <button className="send-button" onClick={sendMessage} title="Send">
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  width="20"
                  height="20"
                  fill="currentColor"
                  viewBox="0 0 16 16"
                >
                  <path d="M15.854.146a.5.5 0 0 1 .11.54l-6 14a.5.5 0 0 1-.948-.032l-2-6-6-2a.5.5 0 0 1 .032-.948l14-6a.5.5 0 0 1 .806.44zM6.832 8.065l1.476 4.427 4.318-10.078L6.832 8.065z" />
                </svg>
              </button>
            </div>
          </div>
        </div>
      </div>

      <div className="footer">ALAASKA: Adaptive Learning</div>
    </>
  );

}

export default App;
