import React, { useState, useEffect } from 'react';
import axios from 'axios';

const BACKEND_URL = "http://localhost:8000";

function App() {
  const [username, setUsername] = useState("");
  const [userInput, setUserInput] = useState("");
  const [chatId, setChatId] = useState(null);
  const [messages, setMessages] = useState([]);
  const [conversations, setConversations] = useState([]);

  useEffect(() => {
    if (username) {
      axios.get(`${BACKEND_URL}/conversations/${username}`)
        .then(res => setConversations(res.data));
    }
  }, [username]);

  const sendMessage = () => {
    if (!userInput.trim()) return; // prevent sending empty messages

    axios.post(`${BACKEND_URL}/chat/`, {
      username,
      message: userInput,
      selected_chat: chatId
    })
    .then(res => {
      setMessages(res.data.history);
      setChatId(res.data.chat_id);
      setUserInput("");
    })
    .catch(err => {
      console.error("Error sending message:", err.response?.data || err.message);
      alert("Failed to send message. Please check console for details.");
    });
  };

  const loadConversation = (file) => {
    axios.get(`${BACKEND_URL}/conversation/${username}/${file}`)
      .then(res => {
        setMessages(res.data);
        setChatId(file);
      });
  };

  const deleteConversation = (file) => {
    axios.delete(`${BACKEND_URL}/conversation/${username}/${file}`)
      .then(() => {
        if (file === chatId) {
          setMessages([]);
          setChatId(null);
        }
        setConversations(conversations.filter(c => c !== file));
      });
  };

  return (
    <div className="container">
      <h1>ALAASKA – AI Tutor</h1>
      <input
        type="text"
        placeholder="Enter your username"
        value={username}
        onChange={e => setUsername(e.target.value)}
      />
      {username && (
        <>
          <div className="sidebar">
            <h3>Conversations</h3>
            {conversations.map(c => (
              <div key={c}>
                <button onClick={() => loadConversation(c)}>{c}</button>
                <button onClick={() => deleteConversation(c)}>❌</button>
              </div>
            ))}
          </div>

          <div className="chatbox">
            <div className="messages">
              {messages.map((msg, i) => (
                <div key={i} className={msg.role}>
                  <strong>{msg.role}:</strong> {msg.content}
                </div>
              ))}
            </div>
            <input
              type="text"
              placeholder="Ask about system programming..."
              value={userInput}
              onChange={e => setUserInput(e.target.value)}
              onKeyDown={e => e.key === 'Enter' && sendMessage()}
            />
            <button onClick={sendMessage}>Send</button>
          </div>
        </>
      )}
    </div>
  );
}

export default App;
